mapper.update:
UPDATE tbl_member SET
	pw = #{pw}
WHERE id = #{id}

UPDATE tbl_board
   SET viewcnt= viewcnt + 1
 WHERE bno = #{bno}

UPDATE tbl_board
   SET goodcnt = #{goodcnt}
 WHERE bno = #{bno}

UPDATE tbl_board
   SET title = #{title},
   	   content = #{content},
   	   filename = #{filename},
   	   filesize = #{filesize}
 WHERE bno = #{bno}
